function a(){if(typeof window>"u")return null;const e=document.getElementById("contact-form"),t=document.getElementById("success-popup"),n=document.getElementById("close-popup");return!e||!t||!n||(e.addEventListener("submit",async o=>{o.preventDefault();const r=new FormData(e);try{await fetch("/",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:new URLSearchParams(r).toString()}),e.reset(),t.classList.remove("hidden")}catch{alert("Submission failed.")}}),n.addEventListener("click",()=>{t.classList.add("hidden")})),null}export{a as default};
